mapper.update:
UPDATE t_exception
full_class_name=#{exception.fullClassName}, description=#{exception.description}, create_date=#{exception.createDate}, last_update=#{exception.lastUpdate}, `status`=#{exception.status}, user_id=#{exception.user.id},
WHERE id = #{exception.id}

mapper.select:
SELECT
e.*,u.id,u.username,u.nickname,u.phone,u.create_date
FROM
(
SELECT
id as exception_id , full_class_name , description , create_date as exception_create_date,
last_update as exception_last_update , status as exception_status , user_id
FROM
t_exception

    status != -1
    AND id = #{exception.id}
    AND full_class_name = #{exception.fullClassName}
    AND status = #{exception.status}
    AND user_id = #{exception.user.id}

        AND ${dateType} BETWEEN #{page.begin} AND #{page.size}


) e
LEFT JOIN
t_user u
ON
e.user_id = u.id

    ORDER BY ${page.sort} ${page.order}



        LIMIT ${page.begin} , ${page.size}

SELECT
COUNT(*)
FROM
t_exception

    status != -1
    AND id = #{exception.id}
    AND full_class_name = #{exception.fullClassName}
    AND status = #{exception.status}
    AND user_id = #{exception.user.id}

        AND ${dateType} BETWEEN #{begin} AND #{end}
